const movieList = {}
let counter = 1;

function addMovie(name) {
    movieList[counter] = name;
    counter++;
    // counter += 1;
    // return name;
}
function showMovie(name) {
    if (Object.keys(movieList).length === 0){
        console.log(`movie is empty`);
    }else {
        console.log(`My Movie List`);
        for (const key in movieList) {
            console.log(`${key}: ${movieList[key]}`);
        }
    }
}
function removeMovie(name) {
    movieList[counter] = name;
    console.log(`movie removed successfully ${name}`);
    counter -= 1;
}
function rateMovie(name) {
    const rate = [];
    for (const key in movieList) {
        rate.push(`${key}: ${movieList[key]}`);
        console.log("movie rated")
    }
    return rate;
}

function movieDoesNotExist(nameOfMovie) {
    const movieListValue =  Object.movieListValue(movieList);
    if (!movieListValue.includes(nameOfMovie)) {
        console.log(`movie not found: ${nameOfMovie}`);
    }else {
        console.log(`movie ${nameOfMovie} exists`);
    }
}

module.exports = {addMovie, showMovie, removeMovie, rateMovie, movieDoesNotExist};

