
const {addMovie, removeMovie, showMovie, rateMovie, movieDoesNotExist} = require('../toDoList/movieApp');

// import addMovie from "../toDoList/movieApp";

const prompt = require("prompt-sync")();

function myMovieApp(){

    let start = true;
    console.log("Starting movie app...");

    while(start) {
        console.log("\n WELCOME TO ZEZE MOVIE SYSTEM")
        console.log("Add Movie");
        console.log("Remove Movie");
        console.log("Show My Movie");
        console.log("Rate Movie");
        console.log("Check If Movie Exist")

        const userChoice = prompt("Choose a movie from (1-5): ");

        switch (userChoice) {
            case "1":
                const name = prompt("Enter your movie name: ");
                addMovie(name);
                console.log(`movie added successfully: ${name}`);
                break;
            case "2":
                removeMovie();
            case "3":
                showMovie();
                break;
            case "4":
                const rate = prompt("Enter movie rate");
                rate = rate + 1;
                break;
            case "5":
                const checkName = prompt("Enter movie name: ");
                movieDoesNotExist(checkName);
                break;

        }
    }
    }
myMovieApp();

